mapper.select:
select count(*)
from awesome_orderlist
where userno_fk = #{userno}

select count(*)
from waitinglist
where userno_fk = #{userno}

select count(*)
from tbl_review
where userno_fk = #{userno}

select cate_no_fk
from wishcategory
where userno_fk = #{userno}

select class_seq, fk_cate_no, fk_teacher_seq, class_title, class_semester, to_char(class_startdate,'yyyy-mm-dd') as class_startdate, to_char(class_enddate, 'yyyy-mm-dd') as class_enddate, class_fee, class_subfee, class_day, class_place, class_personnel, class_time, class_photo, class_content, class_status, class_heart from tbl_class
where class_seq in
${noArr[i]}
order by
${noArr[i]} then ${i}

select userno_fk, class_seq_fk, status, price, to_char(payday, 'yyyy-mm-dd') as payday from ( select o.userno_fk, o.class_seq_fk, o.status, o.price, o.payday, c.class_semester, c.class_startdate from awesome_orderlist o, tbl_class c where o.class_seq_fk = c.class_seq )V
where to_char(class_startdate, 'yyyy') = #{year} and userno_fk = #{userno}
where class_semester in ('3월', '4월', '5월') ('6월', '7월', '8월') ('9월', '10월', '11월') ('12월', '1월', '2월') and userno_fk = #{userno}
where to_char(class_startdate, 'yyyy') = #{year} and class_semester in ('3월', '4월', '5월') ('6월', '7월', '8월') ('9월', '10월', '11월') ('12월', '1월', '2월') and userno_fk = #{userno}
order by payday

select to_char(class_startdate, 'yyyymmdd') as startdate
from awesome_orderlist o, tbl_class c
where o.class_seq_fk = c.class_seq and o.no = #{no}

select teacher_seq, fk_cate_no, teacher_name, teacher_gender
from tbl_teacher
where teacher_seq = #{teacherno}

select teacher_seq, fk_cate_no, teacher_name, teacher_gender
from tbl_teacher
where teacher_seq in 

${teacherArr[i]}

order by

	 ${teacherArr[i]} then ${i}

select class_seq_fk
from awesome_orderlist
where no = #{no}

select hp1||''||hp2||''||hp3
from awesome_member
where userno = #{waitno}

select no, userno_fk, classno_fk, title, content, to_char(writeday, 'yyyy-mm-dd') as writeday
from tbl_review
where userno_fk = #{userno} and classno_fk = #{classno}

select count(*) 
from   awesome_member 
where  userid= #{userid}
